import React from 'react';
import PropTypes from 'prop-types';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import makeStyles from '@material-ui/core/styles/makeStyles';
import { EuroSymbol, PanTool } from '@material-ui/icons';
import FormControl from '@material-ui/core/FormControl';
import InputAdornment from '@material-ui/core/InputAdornment';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles({
  media: {
    height: 260
  }
});

const ProductCard = ({ currentBid, itemName, timeUntilBidEnd }) => {
  const classes = useStyles();

  const adornment = (
    <InputAdornment position="start">
      <EuroSymbol />
    </InputAdornment>
  );

  return (
    <Card style={{ width: `${100}%` }}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image="https://i1.wp.com/thefrontline.org.uk/wp-content/uploads/2018/10/placeholder.jpg"
          title="Contemplative Reptile"
        />
      </CardActionArea>
      <CardContent>
        <Typography gutterBottom variant="h5" component="h2">
          {itemName}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          <Box component="p">{`Current: ${currentBid} Eur`}</Box>
          <Box component="p">{`End after: ${timeUntilBidEnd} Hour`}</Box>
        </Typography>
      </CardContent>
      <CardActions>
        <FormControl fullWidth>
          <Grid container spacing={2}>
            <Grid item md={8}>
              <TextField
                fullWidth
                id="your-bid"
                variant="outlined"
                label="Your bid"
                startAdornment={adornment}
              />
            </Grid>
            <Grid item md={4}>
              <Box mt={0.5}>
                <Button color="primary" fullWidth variant="contained" size="large">
                  <Box mr={2} component={PanTool} />
                  Bid
                </Button>
              </Box>
            </Grid>
          </Grid>
        </FormControl>
      </CardActions>
    </Card>
  );
};

ProductCard.propTypes = {
  currentBid: PropTypes.number.isRequired,
  itemName: PropTypes.string.isRequired,
  timeUntilBidEnd: PropTypes.number.isRequired
};

export default ProductCard;
