import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import SearchIcon from '@material-ui/icons/Search';
import SettingsIcon from '@material-ui/icons/Settings';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';

const categories = [
  {
    value: 'All',
    label: 'All'
  },
  {
    value: 'Computers',
    label: 'Computers'
  },
  {
    value: 'Phones',
    label: 'Phones'
  },
  {
    value: 'Books',
    label: 'Books'
  }
];

const useStyles = makeStyles(theme => ({
  root: {
    marginLeft: theme.spacing(6),
    padding: '2px 4px',
    display: 'flex',
    alignItems: 'center',
    width: 750,
    height: 40,
    backgroundColor: '#f1f3f4',
    boxShadow: 'none',
    flexGrow: 1
    // [theme.breakpoints.down('md')]: {
    //   width: 450
    // },
    // [theme.breakpoints.down('sm')]: {
    //   width: 450
    // }
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1)
    // backgroundColor: 'green'
  },
  menu: {
    width: 200
    // '&:active': {
    //   backgroundColor: 'green'
    // }
  },
  input: {
    marginLeft: 8,
    flex: 1
  },
  iconButton: {
    padding: 6
  },
  divider: {
    width: 1,
    height: 28,
    margin: 4
  }
}));

function Search() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    category: 'All'
  });

  const handleChange = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };

  return (
    <Paper className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper className={classes.paper}>
            <TextField
              select
              label=""
              className={classes.textField}
              value={values.category}
              onChange={handleChange('category')}
              SelectProps={{
                MenuProps: {
                  className: classes.menu
                }
              }}
              InputProps={{
                disableUnderline: true
              }}
            >
              {categories.map(option => (
                <MenuItem key={option.value} value={option.value} button="">
                  {option.label}
                </MenuItem>
              ))}
            </TextField>
            <InputBase className={classes.input} placeholder="Search item..." />
            <IconButton className={classes.iconButton} aria-label="Search" href="">
              <SearchIcon />
            </IconButton>
            <Divider className={classes.divider} />
            <IconButton className={classes.iconButton} aria-label="Directions" href="">
              <SettingsIcon />
            </IconButton>
          </Paper>
        </Grid>
      </Grid>
    </Paper>
  );
}

export default Search;
