import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid } from '@material-ui/core';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import FormControl from '@material-ui/core/FormControl';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputAdornment from '@material-ui/core/InputAdornment';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles(theme => ({
  root: {
    marginTop: 40,
    marginBottom: 40,
    marginRight: theme.spacing(3)
  },
  itemTitle: {
    paddingBottom: 10,
    marginBottom: 10,
    borderBottom: '1px solid #e4e4e4'
  },
  lightText: {
    color: '#636363',
    float: 'right'
  },
  itemPrice: {
    fontWeight: 500
  },
  priceInputField: {
    maxWidth: 120,
    backgroundColor: '#fff'
  },
  button: {
    marginLeft: theme.spacing(1)
  },
  infoArea: {
    paddingTop: theme.spacing(2),
    paddingBottom: theme.spacing(2)
  },
  biddingField: {
    backgroundColor: '#f4f4f4',
    borderRadius: 4,
    paddingTop: theme.spacing(2),
    paddingBottom: theme.spacing(2)
  },
  container: {
    paddingTop: 3,
    paddingBottom: 3
  }
}));

function ItemDescription() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    amount: ''
  });

  const handleChange = prop => event => {
    setValues({ ...values, [prop]: event.target.value });
  };

  return (
    <Paper className={classes.root}>
      <Typography className={classes.itemTitle} variant="h5" component="h3">
        Naujas medvilninis džemperis, M dydis
      </Typography>
      <div className={classes.infoArea}>
        <Grid container spacing={2} className={classes.container}>
          <Grid item md={3}>
            <Typography className={classes.lightText} display="inline" component="p">
              Aprašymas:
            </Typography>
          </Grid>
          <Grid item md={9}>
            <Typography display="inline" component="p">
              Parduodu džemperį M dydžio, moteriškas. Atsiimti galite Vilniuje, su aukciono
              laimėtoju susisieksiu asmeniškai per el. paštą, susitarti dėl atsiėmimo. Į kitus
              miestus nesiunčiu.
            </Typography>
          </Grid>
        </Grid>
        <Grid container spacing={2} className={classes.container}>
          <Grid item md={3}>
            <Typography className={classes.lightText} display="inline" component="p">
              Būklė:
            </Typography>
          </Grid>
          <Grid item md={9}>
            <Typography display="inline" component="p">
              Naujas
            </Typography>
          </Grid>
        </Grid>
        <Grid container spacing={2} className={classes.container}>
          <Grid item md={3}>
            <Typography className={classes.lightText} display="inline" component="p">
              Liko laiko:
            </Typography>
          </Grid>
          <Grid item md={9}>
            <Typography display="inline" component="p">
              1 d 15 h
            </Typography>
          </Grid>
        </Grid>
      </div>
      <div className={classes.biddingField}>
        <Grid container spacing={2} className={classes.container}>
          <Grid item md={3}>
            <Typography className={classes.lightText} display="inline" component="p">
              Dabartinė kaina:
            </Typography>
          </Grid>
          <Grid item md={9}>
            <Typography className={classes.itemPrice} display="inline" variant="h4" component="h4">
              15.50 €
            </Typography>
          </Grid>
        </Grid>
        <Grid container spacing={2} className={classes.container}>
          <Grid item md={3}></Grid>
          <Grid item md={9}>
            <FormControl variant="outlined">
              <OutlinedInput
                id="outlined-adornment-amount"
                className={classes.priceInputField}
                value={values.amount}
                onChange={handleChange('amount')}
                endAdornment={<InputAdornment position="end">€</InputAdornment>}
                aria-describedby="outlined-amount"
                inputProps={{
                  'aria-label': 'amount'
                }}
                labelWidth={0}
              />
            </FormControl>
            <Button variant="contained" color="primary" size="medium" className={classes.button}>
              Siūlyti kainą
            </Button>
          </Grid>
        </Grid>
      </div>
      <div className={classes.infoArea}>
        <Grid container spacing={2} className={classes.container}>
          <Grid item md={3}>
            <Typography className={classes.lightText} display="inline" component="p">
              Pristatymas:
            </Typography>
          </Grid>
          <Grid item md={9}>
            <Typography display="inline" component="p">
              Atsiėmimas asmeniškai <br /> Siuntimas paštu
            </Typography>
          </Grid>
        </Grid>
        <Grid container spacing={2} className={classes.container}>
          <Grid item md={3}>
            <Typography className={classes.lightText} display="inline" component="p">
              Pardavėjas:
            </Typography>
          </Grid>
          <Grid item md={9}>
            <Typography display="inline" component="p">
              Agne123
            </Typography>
            <Typography display="inline" component="p">
              <a href="/#">
                <b>&nbsp;[susisiekti su pardavėju]</b>
              </a>
            </Typography>
          </Grid>
        </Grid>
      </div>
    </Paper>
  );
}

export default ItemDescription;
