        <KeyboardArrowLeft fontSize="large" />
      ) : (
        <KeyboardArrowRight fontSize="large" />
      )}
    </Button>
  }
  backButton={
    <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
      {theme.direction === 'rtl' ? (
        <KeyboardArrowRight fontSize="large" />
